mapper.select:
SELECT
count(*) FROM bbs
WHERE refnum = #{bbsno}

mapper.update:
update bbs
set ansnum =
ansnum + 1
where grpno = #{grpno}
and ansnum > #{ansnum}

UPDATE bbs
wname = #{wname}, title = #{title}, content = #{content},
filename = #{filename}, filesize = #{filesize}
WHERE bbsno = #{bbsno}
